import React, { useState, useEffect } from 'react'

import { Grid, Card, CardHeader, CardContent, Button } from '@material-ui/core'
import { connect } from 'react-redux'

import Objetos from './Objetos'
import { Container } from './styles'
import * as Service from '../../../services/metadados.v2'

function Exclusao() {
  const [objeto, setObjeto] = useState({})
  const [objetos, setObjetos] = useState([])

  useEffect(() => {
    async function fetchObjetos() {
      const response = await Service.fetchObjetoAptoExclusao()
      setObjetos(response.cabecalho)
    }

    fetchObjetos()
  }, [])

  const handleChange = async (event) => {
    const { name, value } = event.target

    const confirm = window.confirm(`Confirma a exclusão do objeto ${value} ?`)

    if (confirm) {
      const response = await Service.excluirObjeto(value)
      if (response.aresposta.codigo === 200) {
        alert('Objeto excluído com sucesso.')
      }
    }
  }

  return (
    <Container>
      <Grid container spacing={1}>
        <Grid item xs={6}>
          <Card>
            <CardHeader
              title='Exclusão'
              style={{ background: '#1E74BB', color: '#FFF' }}
            />
            <CardContent>
              <Objetos data={objetos} value={objeto} onChange={handleChange} />
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  )
}

export default connect()(Exclusao)
