import React, { useState } from 'react'

import {
  Grid,
  CardHeader,
  Card,
  CardContent,
  Button,
  FormHelperText,
} from '@material-ui/core'
import { connect } from 'react-redux'

import { validarMetadado } from '../../../services/metadados'
import { Container, MessageArea } from './styles'

const Validar = ({ validarMetadado }) => {
  const [text, setText] = useState()

  const handleValidar = async () => {
    setText(['Em processamento. Pode demorar alguns minutos...'])

    const response = await validarMetadado()

    setText(response.cabecalho)
  }

  return (
    <Container>
      <Grid container spacing={1}>
        <Grid item xs={6}>
          <Card>
            <CardHeader
              title='Validação'
              style={{ background: '#1E74BB', color: '#FFF' }}
            />
            <CardContent>
              <Button variant='contained' onClick={handleValidar}>
                Validar Objetos
              </Button>
              <MessageArea>{JSON.stringify(text)}</MessageArea>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  )
}

const mapStateToProps = () => ({})

const mapDispatchToProps = (dispatch) => ({
  validarMetadado: () => dispatch(validarMetadado()),
})

export default connect(mapStateToProps, mapDispatchToProps)(Validar)
